INSERT INTO `topics` (`id`, `name`, `description`, `quantity_group_max`, `note`, `target`, `expected_result`, `standard_output`, `require_input`, `status`, `created_at`, `updated_at`, `lecturer_term_id`) VALUES
	('1aabe8c8-9e5a-4c89-bf8e-fbb61eac3b2d', 'Quản lý khách sạn', 'Chương trình giúp người quản lý quản lý được nhân viên, quản lý việc đặt trả phòng của khách... và khách hàng đặt được phòng.', 5, NULL, '- Giúp người quản lý, quản lý được khách sạn\r\n- Giúp khách hàng xem thông tin về phòng, đặt phòng...', 'Áp dụng được vào thực tế', 'A. Sinh viên tham gia đề tài\r\n1) Nắm vững kiến thức, kỹ năng lập trình C/C++ hoặc Java\r\n2) Có kiến thức, kỹ năng triển khai dự án phần mềm đầy đủ qui trình từ Requirement => Design => Coding => Unit Testing\r\n3) Có năng lực đầy đủ về Lập kế hoạch, theo dõi tiến độ, phân tích các vấn đề phát sinh trong quá trình thực hiện dự án.\r\n4) Đọc tài liệu tiếng Anh, tìm kiếm tài liệu, Kỹ năng viết báo cáo, tài liệu thuyết trình\r\n5) Có thể thu thập, phân tích được hành vi người dùng trong quá trình thực hiện dự án.\r\nB. Sản phẩm\r\n1) Có tài liệu mô tả Yêu cầu dự án.\r\n2) Có tài liệu mô tả Thiết kế kiến trúc của dự án.\r\n3) Có tài liệu mô tả Thiết kế chi tiết cho chức năng chính của dự án.\r\n4)  Có tài liệu mô tả tình huống test và kết quả test cho chức năng chính của dự án.\r\n5) Có tài liệu mô tả cách biên dịch, đóng gói và hướng dẫn nâng cấp mã nguồn cho dự án.', 'Có kiến thức về quản lý, báo cáo Crystal report.\r\n- Kỹ năng phân tích thiết kế, lập trình tốt.\r\n- Cần cù, chiu khó.\r\n- Có tinh thần ham học hỏi, cầu tiến.', 'PENDING', '2024-07-31 12:05:54', '2024-07-31 12:05:54', 'f3004d76-4935-4713-8e7f-9542af2faffd'),
	('2490ad11-9833-466f-a219-ff1bff6e9fc2', 'Website giới thiệu phim mới', '- Quản lý nội dung phim, thể loại phim sắp phát hành\r\n- Thống kế lượng truy cập, đánh giá khảo sát phim\r\n- Quản lý thông tin người dùng', 5, NULL, '- Hiểu được kiến trúc tổng thể hệ thống giới thiệu phim mới\r\n- Sử dụng được các công nghệ lập trình Node JS, HTML5, CSS3, jQuery, Ajax \r\n- Biết cách khai thác và triển khai ứng dụng trên nền tảng điện toán đám mây', 'Hiện thực ứng dụng trực tuyến trên nền tảng đám mây (AWS), Các chức năng ứng dụng gần giống thực tế', 'A.Sinh viên tham gia đề tài.\r\n1) Có khả năng triển khai ứng dụng web trên nền tảng điện toán đám mây\r\n2) Có kiến thức và kỹ năng lập trình web và khai thác các dịch vụ điện toán đám mây\r\n3) Thực hiện theo qui trình từ Requirement => Design => Coding => Unit Testing. \r\n4) Kế hoạch thực hiện đề tài\r\nB.Sản phẩm\r\n1) Có thể sử dụng được, có thể phát triển tiếp\r\n2) Tài liệu thực hiện theo mẫu đồ án quy định \r\n3) Mả nguồn chương trình\r\n4) Tài liệu mô tả qui trình testing', '- Hiểu nền tảng kiến trúc và khai thác dịch vụ điện toán đám mây (AWS)\r\n- Hiểu và có kỹ năng lập trình Node.js và các công nghệ web có liên quan', 'PENDING', '2024-07-31 12:05:54', '2024-07-31 12:05:54', '5eb744e0-3c2a-4167-8e6e-c629dda38ee2'),
	('32dc7eb4-8bc5-44e7-8012-39ba291534f4', 'Xây dựng ứng dụng giúp đặt món ăn trên thiết bị di động', '-	Hỗ trợ việc quản lí, kiểm tra doanh thu, tình hình nguyên vật liệu\r\n-        Tạo ra một hệ thống thông tin liền mạch từ nhân viên phục vụ, bộ phận pha chế, nhà bếp đến bộ phận thu ngân và quản lí\r\n-        Giúp khách hàng có thể đặt món từ xa, dùng dữ liệu có sẵn để có thể tạo sự tiện lợi tối đa cho khách hàng', 5, NULL, '- Lập trình được trên HDH Androi, ứng dụng được các công cụ (thư viện, các gói mã nguồn mở...) vào thực hiện trong đề tài \r\n- Phân tích, thiết kế ứng dụng . \r\n- Hiện thực được ứng dụng, triễn khai được thành các App trên nền tảng android', 'Hiện thực và triễn khai được thành các App trên nền tảng android.', 'A.Sinh viên tham gia đề tài \r\n1) Nắm vững kiến thức, kỹ năng lập trình Java, lập trình TBDĐ trên Android \r\n2) Đọc tài liệu tiếng Anh, tìm kiếm tài liệu, Kỹ năng viết báo cáo, tài liệu thuyết trình\r\n3) Có năng lực đầy đủ về Lập kế hoạch, theo dõi tiến độ, phân tích các vấn đề phát sinh trong quá trình thực hiện đề tài. \r\n4) Có kỹ năng sử dụng tích hợp công nghệ mới và đề tài. \r\nB.Sản phẩm \r\n1) Có tài liệu mô tả Yêu cầu dự án. \r\n2) Có tài liệu mô tả Thiết kế kiến trúc của dự án. \r\n3) Có tài liệu mô tả Thiết kế chi tiết cho chức năng chính của dự án.\r\n 4) Có mã nguồn được lưu vết (tracking) định kỳ trên hệ thống quản lý phiên bản (version control) như Subversion hoặc Git; Mã nguồn trình bày rõ ràng, dễ hiểu theo tiêu chuẩn đã được thống nhất. \r\n5) Có tài liệu mô tả tình huống test và kết quả test cho chức năng chính của dự án. \r\n6) Có tài liệu mô tả cách biên dịch, đóng gói và hướng dẫn nâng cấp mã nguồn cho dự án.', '- Có kiến thức lập trình trên thiết bị di động.\r\n - Có tinh thần học hỏi và mong muốn làm sản phẩm theo quy trình công nghiệp: tuân thủ tiêu chuẩn khi viết mã nguồn, có hoạt động đảm bảo chất lượng mã nguồn.\r\n - Biết cách tìm hiểu nhu cầu thực tế của người dùng.\r\n - Có khả năng viết mô tả yêu cầu ứng dụng.', 'PENDING', '2024-07-31 12:05:54', '2024-07-31 12:05:54', '45d71fdc-b443-490a-9e62-972ee650ca3f'),
	('461e42c0-1aa5-45e5-98fc-22f32ba48420', 'Tạo ra ứng dụng về du lịch và dịch vụ bán sản phẩm.', '- Tìm hiểu digital marketing.\r\n - Nguyên cứu các công nghệ NodeJS, VueJS, HTML5, CSS, SCSS, Javascript, Boostrap, MongoDB.\r\n - Nguyên cứu cách viết và xây dựng ứng dụng cho số lượng lớn truy cập.\r\n - Nguyên cứu cách viết ứng dụng tích hợp với các mạng xã hội.\r\n - Nguyên cứu cách tạo ra các con bot Crawl Data.', 5, NULL, '- Hiểu được khái niệm, cách tổ chức 1 trang du lịch và thương mại điện tử.\r\n - Sử dụng được các công nghệ: NodeJS, VueJS, HTML5, CSS, SCSS, Javascript, Boostrap, MongoDB.\r\n -Vận dụng được năng lực lập trình để xây dựng sản phẩm theo chuẩn công nghiệp.\r\n - Triển khai hệ thống trên Cloud.\r\n - Tạo ra các con bot Crawl Data từ các trang liên quan.', '-Phần mềm cho phép người dùng nắm bắt thông tin cơ bản và chuẩn bị đồ dùng cần thiết cho chuyến đi, đăng bài, tạo bộ sưu tập, bình luận,tạo lịch các chuyến đi của chính mình.\r\n - Thông báo sự kiện qua Gmail(Thông tin địa điểm thông qua lịch được đặt sẵn).\r\n - Phần mềm có thêm 1 dịch vụ mua sản phẩm liên quan.', 'A.Sinh viên tham gia đề tài \r\n1) Nắm vững kiến thức, kỹ năng lập trình Nodejs, Html, Css, Scss, Vuejs \r\n2) Có kiến thức, kỹ năng triển khai dự án phần mềm đầy đủ qui trình từ Requirement => Design => Coding => Unit Testing \r\n3) Có năng lực đầy đủ về Lập kế hoạch, theo dõi tiến độ, phân tích các vấn đề phát sinh trong quá trình thực hiện dự án.\r\n 4) Có thể thu thập thông tin và phân tích các vấn đề phát sinh. B.Sản phẩm\r\n 1) Có tài liệu mô tả Yêu cầu dự án.\r\n 2) Có tài liệu mô tả Thiết kế kiến trúc của dự án.\r\n 3) Có tài liệu mô tả Thiết kế chi tiết cho chức năng chính của dự án. \r\n4) Có mã nguồn được lưu vết (tracking) định kỳ trên hệ thống quản lý phiên bản (version control) như Subversion hoặc Git; Mã nguồn trình bày rõ ràng, dễ hiểu theo tiêu chuẩn đã được thống nhất.\r\n 5) Có tài liệu mô tả tình huống test và kết quả test cho chức năng chính của dự án. \r\n6) Có tài liệu mô tả cách biên dịch, đóng gói và hướng dẫn nâng cấp mã nguồn cho dự án.', '- Đam mê nghiên cứu công nghệ, thuật toán.\r\n - Có kiến thức cơ bản về Web Programming.\r\n - Kiến thức lập trình Javascript, Html, Css,\r\n - Có tinh thần học hỏi và mong muốn làm sản phẩm theo quy trình công nghiệp: tuân thủ tiêu chuẩn khi viết mã nguồn, có hoạt động đảm bảo chất lượng mã nguồn', 'PENDING', '2024-07-31 12:05:54', '2024-07-31 12:05:54', '81acc68e-b27d-409f-818d-2b9e072d2f69'),
	('4bf0d684-2abc-43da-ab59-034e98726e7b', 'Xây dựng Website buôn bán online', '-"- Giúp người dùng ở tại nhà nhưng vẫn mua bán được các sản phẩm trên gian hàng.\r\n- Tiết kiệm được không gian và thời gian không phải tập trung\r\nđến chợ và siêu thị"', 5, 'Đề tài không phù hợp', '- Xây dựng website bán hàng trực tuyến:\r\n-Giúp người dùng tham bán và mua hàng online ;\r\n-có thể chọn: \r\n+Gian hàng, thương hiệu sản phẩm, được cung cấp Website', 'Hiện thực và triển khai được các gian hàng online', 'Chức năng Website: người dùng có thể mua hoặc đăng ký bán các mặt hàng trên gian hàng trên web.\r\n Hệ thống phân tích tự động khuyến khích khách hàng chọn mua những sản phẩm cần thiết phù hợp: mặt hàng giảm giá nhiều nhất, mua nhiều nhất, giao dịch thường xuyên, giao dịch thành viên....\r\nSinh viên thực hiện đề tài:\r\n- Phân tích thiết kế hệ thống.\r\n- Nắm vững kiến thức Html, Css,...\r\n- Có kiến thức, kỹ năng triển khai dự án theo qui trình chặt chẽ \r\n- Biết lập kế hoạt và triển khai dự án, theo dõi tiến độ, phân tích các vấn đề phát sinh trong quá trình thực hiện dự án.\r\n - Hoàn thành dự án trên cơ sở phân tích yêu cầu của người dùng và kiểm thử chặt chẽ. \r\nKết quả Sản phẩm:\r\n - Xây dựng được Website theo yêu cầu, tài liệu  thiết kế kiến trúc của dự án.\r\n - Triển khai cài đặt ứng dụng trên internet', 'Người quản trị khởi tạo và cung cấp tài khoản cho\r\nCác hộ kinh doanh theo các gian hàng online. \r\nĐược phân quyền theo các chức năng cập nhật thêm xóa sửa các mặt hàng trên gian hàng\r\nKhác mua hàng có thể đăng ký thành viên qua hệ thống, người mua hàng được chọn các mặt hàng và thanh toán tiền khi đồng ý.', 'REJECTED', '2024-07-31 12:05:54', '2024-07-31 12:27:46', '6cec4b21-9440-43ba-ad41-3cc909d69793'),
	('4d61b6b3-57f7-4789-90f3-8cc469d6d9d1', 'Quản lý đặt bàn trong nhà hàng', 'Chương trình giúp người quản lý quản lý được  việc đặt món ăn, tính tiền của khách... và khách hàng đặt được món ăn', 5, NULL, 'Giúp người quản lý, quản lý được bàn đặt, các món ăn \r\n- Giúp khách hàng xem thông tin về món ăn, đặt bàn...', 'Áp dụng được vào thực tế', 'A. Sinh viên tham gia đề tài\r\n1) Nắm vững kiến thức, kỹ năng lập trình C/C++ hoặc Java\r\n2) Có kiến thức, kỹ năng triển khai dự án phần mềm đầy đủ qui trình từ Requirement => Design => Coding => Unit Testing\r\n3) Có năng lực đầy đủ về Lập kế hoạch, theo dõi tiến độ, phân tích các vấn đề phát sinh trong quá trình thực hiện dự án.\r\n4) Đọc tài liệu tiếng Anh, tìm kiếm tài liệu, Kỹ năng viết báo cáo, tài liệu thuyết trình\r\n5) Có thể thu thập, phân tích được hành vi người dùng trong quá trình thực hiện dự án.\r\nB. Sản phẩm\r\n1) Có tài liệu mô tả Yêu cầu dự án.\r\n2) Có tài liệu mô tả Thiết kế kiến trúc của dự án.\r\n3) Có tài liệu mô tả Thiết kế chi tiết cho chức năng chính của dự án.\r\n4)  Có tài liệu mô tả tình huống test và kết quả test cho chức năng chính của dự án.\r\n5) Có tài liệu mô tả cách biên dịch, đóng gói và hướng dẫn nâng cấp mã nguồn cho dự án.', 'Có kiến thức về quản lý, báo cáo Crystal report.\r\n- Kỹ năng phân tích thiết kế, lập trình tốt.\r\n- Cần cù, chiu khó.\r\n- Có tinh thần ham học hỏi, cầu tiến.', 'PENDING', '2024-07-31 12:05:54', '2024-07-31 12:05:54', 'f3004d76-4935-4713-8e7f-9542af2faffd'),
	('5f873746-583d-4713-97e8-36b8c6f178a0', 'Xây dựng App đặt đồ uống qua Mobile (Android) cho quán cà phê CHB. Backend C# ASP.NET MVC hoặc PHP', '- Tìm hiểu nghiệp vụ hoạt động của Coffe Shop.\r\n- Nghiên cứu công nghệ lập trình Java Mobile kết hợp ASP.NET MVC hoặc PHP phía backend.\r\n- Phát triển các module: - Đặt món\r\n                                       - Thu nhận yêu cầu của Tiếp tân\r\n                                       - Bộ phận Bếp xử lý đơn và trả kết quả.\r\n                                       - Thống kê doanh thu\r\n(Và một số module phụ để phục vụ các chức năng chính trên như: loại đồ uống v.v.)', 5, NULL, '- Lập trình được trên HDH Android.\r\n- Giúp cho Sinh viên cập nhật những kiến thực về Thương mại Online.\r\n- Triển khai được một App bán hàng.\r\n- Giúp cho một Đơn vị nhỏ có thể phân phối Sản phẩm của mình qua Mobile', '- Triển khai được App ứng dụng cho một quán Cà phê cụ thể.', 'A.Sinh viên tham gia đề tài\r\n1) Nắm vững kiến thức, kỹ năng lập trình  Java\r\n2) Có kiến thức, kỹ năng triển khai dự án phần mềm đầy đủ qui trình từ Requirement => Design => Coding => Unit Testing\r\n3) Có kỹ năng lập trình sử dụng thư viện ngoài.\r\nB.Sản phẩm\r\n1) Có tài liệu mô tả Yêu cầu dự án.\r\n2) Có tài liệu mô tả Thiết kế chi tiết cho chức năng chính của dự án.\r\n3) Có mã nguồn được lưu vết (tracking) định kỳ trên hệ thống quản lý phiên bản (version control) như Subversion hoặc Git; Mã nguồn trình bày rõ ràng.\r\n4) Có tài liệu mô tả tình huống test và kết quả test cho chức năng chính của dự án.\r\n5) Có tài liệu mô tả cách biên dịch, đóng gói và hướng dẫn nâng cấp mã nguồn cho dự án.', '- Đam mê nghiên cứu công nghệ.\r\n- Kiến thức lập trình Java Mobile.\r\n- Có kiến thức cơ bản về Git để quản lý mã nguồn theo phiên bản.\r\n- Có tinh thần học hỏi và mong muốn làm sản phẩm.', 'PENDING', '2024-07-31 12:05:54', '2024-07-31 12:05:54', '1369d846-4aac-402e-8e99-ee9b6a57e55b'),
	('64bf1ff2-8407-42dc-9223-accf54b47b66', 'Website kinh doanh bất động sản', '- Khảo sát các website\r\n- Tạo sản phẩm hỗ trợ cho việc kinh doanh', 5, NULL, '- Giới Thiệu được công ty, dịch vụ và sản phẩm\r\n- Hỗ trợ cho việc kinh doanh bất động sản', 'Tương tự website thực tế', 'A.Sinh viên tham gia đề tài.\r\n1) Có khả năng phát triển ứng dụng web.\r\n3) Thực hiện theo qui trình từ Requirement => Design => Coding => Unit Testing.\r\nB.Sản phẩm\r\n1) Có thể sử dụng được, có thể phát triển tiếp\r\n2) Có chức năng trung gian mua bán, tìm kiếm nâng cao, quản lý đối tác\r\n3) Tài liệu Thực hiện theo mẫu đồ án quy định', '- Có suy nghĩ thực tế\r\n- Lập trình web', 'PENDING', '2024-07-31 12:05:54', '2024-07-31 12:05:54', '4aba9496-c996-492f-9c63-251dad468ea1'),
	('69e80e86-82b6-48f6-92f6-9abf8dddf4e1', 'Xây dựng ứng dụng quản lý tài chính cá nhân của sinh viên trên thiết bị di động', '-Đơn giản hóa công việc quản lý tài chính : Thay vì Sv phải viết ra giấy chi tiêu của mình  thì Sv có thể nhập chi tiêu hằng ngày vào phần mềm này,phần mềm sẽ tự động tính tổng chi tiêu cuối ngày và cuối tháng cho Sv.\r\n- Nhắc nhớ chi tiêu hằng ngày\r\n- Lập kế hoạch tiết kiệm cho sự kiện sắp tới.', 5, NULL, '- Lập trình được trên HDH Androi, ứng dụng được các công cụ (thư viện, các gói mã nguồn mở...) vào thực hiện trong đề tài \r\n- Phân tích, thiết kế ứng dụng . \r\n- Hiện thực được ứng dụng, triễn khai được thành các App trên nền tảng android', 'Hiện thực và triễn khai được thành các App trên nền tảng android.', 'A.Sinh viên tham gia đề tài \r\n1) Nắm vững kiến thức, kỹ năng lập trình Java, lập trình TBDĐ trên Android \r\n2) Đọc tài liệu tiếng Anh, tìm kiếm tài liệu, Kỹ năng viết báo cáo, tài liệu thuyết trình\r\n3) Có năng lực đầy đủ về Lập kế hoạch, theo dõi tiến độ, phân tích các vấn đề phát sinh trong quá trình thực hiện đề tài. \r\n4) Có kỹ năng sử dụng tích hợp công nghệ mới và đề tài. \r\nB.Sản phẩm \r\n1) Có tài liệu mô tả Yêu cầu dự án. \r\n2) Có tài liệu mô tả Thiết kế kiến trúc của dự án. \r\n3) Có tài liệu mô tả Thiết kế chi tiết cho chức năng chính của dự án.\r\n 4) Có mã nguồn được lưu vết (tracking) định kỳ trên hệ thống quản lý phiên bản (version control) như Subversion hoặc Git; Mã nguồn trình bày rõ ràng, dễ hiểu theo tiêu chuẩn đã được thống nhất. \r\n5) Có tài liệu mô tả tình huống test và kết quả test cho chức năng chính của dự án. \r\n6) Có tài liệu mô tả cách biên dịch, đóng gói và hướng dẫn nâng cấp mã nguồn cho dự án.', '- Có kiến thức lập trình trên thiết bị di động.\r\n - Có tinh thần học hỏi và mong muốn làm sản phẩm theo quy trình công nghiệp: tuân thủ tiêu chuẩn khi viết mã nguồn, Có hoạt động đảm bảo chất lượng mã nguồn.\r\n - Biết cách tìm hiểu nhu cầu thực tế của người dùng.\r\n - Có khả năng viết mô tả yêu cầu ứng dụng.', 'PENDING', '2024-07-31 12:05:54', '2024-07-31 12:05:54', '45d71fdc-b443-490a-9e62-972ee650ca3f'),
	('6b68a95f-6eb2-4f3a-b9c5-7e7f916c6a27', 'Tìm hiểu công nghệ WPF và ứng dụng xây dựng game ghép hình cho trẻ em', '- Tìm hiểu cách thức lập trình với WPF. \r\n - Tìm hiểu các control, các layout.\r\n - Tìm hiểu style, template.\r\n - Tìm hiểu đồ họa 2D, 3D.\r\n - Tìm hiểu khả năng tạo hiệu ứng, âm thanh, hình ảnh, hoạt họa của WPF.\r\n - Mô tả rõ ràng yêu cầu của game ghép hình cho trẻ em, phân tích, thiết kế, cài đặt game.\r\n Tham khảo:\r\n https://www.youtube.com/watch?v=NJs96fs56Ho\r\n https://www.youtube.com/watch?v=gy5qxtsvzxE\r\n https://www.youtube.com/watch?v=vTXPPozt6ao\r\n https://www.youtube.com/watch?v=W40CCYU4ZBM\r\n https://9mobi.vn/game-xep-hinh-cho-be-tren-ios-706n.aspx', 5, NULL, '- Hiểu được cách thức lập trình với WPF.\r\n - Vận dụng được năng lực lập trình C# để xây dựng sản phẩm theo tiêu chuẩn công nghiệp.', 'Game ghép hình cho trẻ em', 'A.Sinh viên tham gia đề tài\r\n1) Nắm vững kiến thức, kỹ năng lập trình C#.\r\n2) Có kiến thức, kỹ năng triển khai dự án phần mềm đầy đủ qui trình từ Requirement => Design => Coding => Unit Testing.\r\n3) Có năng lực đầy đủ về lập kế hoạch, theo dõi tiến độ, phân tích các vấn đề phát sinh trong quá trình thực hiện dự án.\r\nB.Sản phẩm\r\n1) Tài liệu mô tả yêu cầu.\r\n2) Tài liệu mô tả thiết kế.\r\n3) Tài liệu mô tả thiết kế chi tiết cho chức năng chính.\r\n4) Mã nguồn (được lưu vết định kỳ trên hệ thống quản lý phiên bản như SubVersion hoặc Git; mã nguồn trình bày rõ ràng, dễ hiểu theo tiêu chuẩn đã được thống nhất).\r\n5) Tài liệu mô tả tình huống test và kết quả test cho chức năng chính của dự án.\r\n6) Tài liệu mô tả cách biên dịch, đóng gói và hướng dẫn nâng cấp mã nguồn cho dự án.', '- Đam mê nghiên cứu công nghệ, lập trình.\r\n - Có kiến thức lập trình C#.\r\n - Có tinh thần học hỏi và mong muốn làm sản phẩm theo quy trình công nghiệp: tuân thủ tiêu chuẩn khi viết mã nguồn, có hoạt động đảm bảo chất lượng mã nguồn.\r\n - Có khả năng viết mô tả yêu cầu ứng dụng.', 'PENDING', '2024-07-31 12:05:54', '2024-07-31 12:05:54', '10dff8f5-fb4f-4bae-a97c-5445bb623a49'),
	('6c80d232-0d83-48da-8989-69804a08560b', 'Sàn giao dịch hàng hóa online', '- Khảo sát các website\r\n- Lựa chọn các chức năng cho loại giao dịch\r\n- Tạo sản phẩm với vai trò trung gian giao dịch', 5, NULL, '- Hiểu được vai trò trung gian của sàn giao dịch\r\n- Bảo đảm an toàn giao dịch\r\n- Website cho phép người dùng giao dịch online thuận lợi', 'Tương tự các sàn giao dịch trong thực tế', 'A.Sinh viên tham gia đề tài.\r\n1) Có khả năng phát triển ứng dụng web.\r\n3) Thực hiện theo qui trình từ Requirement => Design => Coding => Unit Testing.\r\nB.Sản phẩm\r\n1) Có thể sử dụng được, có thể phát triển tiếp\r\n2) Tất cả các giao dịch mua bán đều qua trung gian, tài khoản của sàn\r\n3) Tài liệu Thực hiện theo mẫu đồ án quy định', '- Phân tích nghiệp vụ tốt\r\n- Lập trình web', 'PENDING', '2024-07-31 12:05:54', '2024-07-31 12:05:54', '4aba9496-c996-492f-9c63-251dad468ea1'),
	('707fa961-bcdf-464b-b2e0-4b88ef144889', 'Website giới thiệu phim mới', '- Tìm hiểu cách thức lập trình với WPF. \r\n - Tìm hiểu các control, các layout.\r\n - Tìm hiểu style, template.\r\n - Tìm hiểu đồ họa 2D, 3D.\r\n - Tìm hiểu khả năng tạo hiệu ứng, âm thanh, hình ảnh, hoạt họa của WPF.\r\n - Mô tả rõ ràng yêu cầu của game ghép hình cho trẻ em, phân tích, thiết kế, cài đặt game.\r\n Tham khảo:\r\n https://www.youtube.com/watch?v=NJs96fs56Ho\r\n https://www.youtube.com/watch?v=gy5qxtsvzxE\r\n https://www.youtube.com/watch?v=vTXPPozt6ao\r\n https://www.youtube.com/watch?v=W40CCYU4ZBM\r\n https://9mobi.vn/game-xep-hinh-cho-be-tren-ios-706n.aspx', 5, NULL, 'Hiểu được cách thức lập trình với Mern stack.', 'Sử dụng để quản lý khóa luận cho toàn khoa', 'A.Sinh viên tham gia đề tài\r\n1) Nắm vững kiến thức, kỹ năng lập trình C#.\r\n2) Có kiến thức, kỹ năng triển khai dự án phần mềm đầy đủ qui trình từ Requirement => Design => Coding => Unit Testing.\r\n3) Có năng lực đầy đủ về lập kế hoạch, theo dõi tiến độ, phân tích các vấn đề phát sinh trong quá trình thực hiện dự án.\r\nB.Sản phẩm\r\n1) Tài liệu mô tả yêu cầu.\r\n2) Tài liệu mô tả thiết kế.\r\n3) Tài liệu mô tả thiết kế chi tiết cho chức năng chính.\r\n4) Mã nguồn (được lưu vết định kỳ trên hệ thống quản lý phiên bản như SubVersion hoặc Git; mã nguồn trình bày rõ ràng, dễ hiểu theo tiêu chuẩn đã được thống nhất).\r\n5) Tài liệu mô tả tình huống test và kết quả test cho chức năng chính của dự án.\r\n6) Tài liệu mô tả cách biên dịch, đóng gói và hướng dẫn nâng cấp mã nguồn cho dự án.', '- Đam mê nghiên cứu công nghệ, lập trình.\r\n - Có kiến thức lập trình C#.\r\n - Có tinh thần học hỏi và mong muốn làm sản phẩm theo quy trình công nghiệp: tuân thủ tiêu chuẩn khi viết mã nguồn, có hoạt động đảm bảo chất lượng mã nguồn.\r\n - Có khả năng viết mô tả yêu cầu ứng dụng.', 'PENDING', '2024-08-19 15:59:25', '2024-08-19 15:59:25', 'ab9ec7bb-683f-4c85-a5d3-b9889d4c0a79'),
	('8cb5c30e-ad71-4a3d-901f-22fae3f846dd', 'Xây dựng Website là một diễn đàn chia sẻ các kinh nghiệm \r\nchăm sóc con của cha mẹ', '- Tìm hiểu rõ ràng và đưa ra các yêu cầu về nhu câu cần trao đổi và chia sẻ của các cha mẹ \r\nvề con cái của họ như tính tình, cách dạy dỗ, giáo dục giới tính ....\r\n- Từ đó dẫn đến việc phân tích CSDL hiện thực đúng đắn\r\n- Áp dụng được các kiến thức đã học để thiết kế và xây dựng website. \r\n- SV có thể tìm hiểu thêm các kiến thức khác áp dụng cho việc tạo 1 Website', 5, NULL, '- Phân tích công việc và các thông tin cần trao đổi trên website\r\n- Sử dụng được các công nghệ lập trình Node JS, HTML5, CSS3, jQuery, Ajax \r\n- Thực thi được kết nối với các trang mạng xã hội khác trong diễn đàn như \r\nfacebook, twitter, instagram ...', 'Website sau khi hoàn thành phải có tính thực tế \r\nvà áp dụng trên đa nền tảng khác nhau', 'A.Sinh viên tham gia đề tài\r\n1) Nắm vững kiến thức, kỹ năng thiết kế Web và lập trình C#.\r\n2) Có kiến thức, kỹ năng triển khai dự ánwebsite đầy đủ qui trình từ Requirement => Design => Coding => Unit Testing=>Maintaining.\r\n3) Có năng lực đầy đủ về lập kế hoạch, theo dõi tiến độ, phân tích các vấn đề phát sinh trong quá trình thực hiện dự án.\r\nB.Sản phẩm\r\n1) Tài liệu Báo cáo khóa luận theo mẫu quy định của FIT IUH.\r\n2) Tài liệu mô tả yêu cầu.\r\n3) Tài liệu mô tả thiết kế.\r\n4) Tài liệu mô tả thiết kế chi tiết cho chức năng chính.\r\n5) Mã nguồn (được lưu vết định kỳ trên hệ thống quản lý phiên bản như SubVersion hoặc Git; mã nguồn trình bày rõ ràng, dễ hiểu theo tiêu chuẩn đã được thống nhất).\r\n7) Tài liệu mô tả tình huống test và kết quả test cho chức năng chính của dự án.\r\n8) Tài liệu mô tả cách triển khai Website\r\n9) Tài liệu hướng dẫn nâng cấp mới theo nhu cầu', '- SV có đam mê về lập trình và phân tích hệ thống \r\n-SV có kiến thức về lập trình Web\r\n- SV phải chịu khó học hỏi và có tinh thần làm việc tự giác', 'PENDING', '2024-07-31 12:05:54', '2024-07-31 12:05:54', 'f22a0d72-1275-4f29-